const cards = [
  { value: 25 },
  { value: 25 },
  { value: 50 },
  { value: 50 },
  { value: 50 },
  { value: 100 },
  { value: 200 },
  { value: 0 },
  { value: 0 },
  { value: 0 },
  { value: 0 },
  { value: 0 }
];

let selectedCards = [];
let totalScore = 0;

const gameContainer = document.getElementById('game-container');
const progressBar = document.getElementById('progress-bar');
const progressCount = document.getElementById('progress-count');
const progressFill = document.getElementById('progress-fill');
const getBtn = document.getElementById('new-game-btn');

const modalEmpty = document.querySelector('.modal-empty');
const modalPart = document.querySelector('.modal-part');
const modalAll = document.querySelector('.modal-all');

// Shuffle cards
function shuffleCards() {
  cards.sort(() => Math.random() - 0.5);
}

// Initialize game
function initGame() {
  getBtn.classList.add('hidden');
  modalEmpty.classList.add('visibility');
  modalPart.classList.add('visibility');
  modalAll.classList.add('visibility');
  gameContainer.innerHTML = '';
  progressCount.innerText = '';
  totalScore = 0;
  selectedCards = [];
  progressFill.style.width = '0';
  renderCards();
  shuffleCards();
  // console.log(cards);
}

// Render cards on the page
function renderCards() {
  cards.forEach((card, index) => {
    const cardElement = document.createElement('div');
    cardElement.classList.add('card');
    cardElement.dataset.index = index;
    cardElement.addEventListener('pointerdown', revealCard);

    const cardImageTop = Object.assign(document.createElement('canvas'), {
      width: 161,
      height: 225,
    }),
      ctx = cardImageTop.getContext('2d'),
      image = new Image();

    cardImageTop.classList.add('top');

    //b64
    image.src = 'data:image/png;base64,';

    image.onload = function () {
      ctx.drawImage(image, 0, 0, 161, 225);
    };

    const cardImageBottom = document.createElement('img');
    cardImageBottom.classList.add('bottom');

    cardElement.appendChild(cardImageTop);
    cardElement.appendChild(cardImageBottom);
    gameContainer.appendChild(cardElement);
  });
};

// Reveal card
function revealCard(e) {
  const index = e.currentTarget.dataset.index;

  let isDrawing, lastPoint;
  const cardElement = gameContainer.children[index],
    canvas = cardElement.querySelector('.top'),
    canvasWidth = canvas.width,
    canvasHeight = canvas.height,
    ctx = canvas.getContext('2d'),
    brush = new Image();

  //b64
  brush.src = 'data:image/png;base64,';

  function addSrc() {
    if (cards[index].value == 25) {
      cardElement.querySelector('.bottom').src = '25.png';
    } else if (cards[index].value == 50) {
      cardElement.querySelector('.bottom').src = '50.png';
    } else if (cards[index].value == 100) {
      cardElement.querySelector('.bottom').src = '100.png';
    } else if (cards[index].value == 200) {
      cardElement.querySelector('.bottom').src = '200.png';
    } else {
      cardElement.querySelector('.bottom').src = 'empty.png';
    };
  };

  canvas.addEventListener('mousedown', handleMouseDown, false);
  canvas.addEventListener('touchstart', handleMouseDown, false);
  canvas.addEventListener('mousemove', handleMouseMove, false);
  canvas.addEventListener('touchmove', handleMouseMove, false);
  canvas.addEventListener('mouseup', handleMouseUp, false);
  canvas.addEventListener('touchend', handleMouseUp, false);


  function distanceBetween(point1, point2) {
    return Math.sqrt(Math.pow(point2.x - point1.x, 2) + Math.pow(point2.y - point1.y, 2));
  }

  function angleBetween(point1, point2) {
    return Math.atan2(point2.x - point1.x, point2.y - point1.y);
  }

  // Only test every `stride` pixel
  function getFilledInPixels(stride) {
    if (!stride || stride < 1) { stride = 1; }

    let pixels = ctx.getImageData(0, 0, canvasWidth, canvasHeight),
      pdata = pixels.data,
      l = pdata.length,
      total = (l / stride),
      count = 0;
    // console.log(pixels)
    // Iterate over all pixels
    for (let i = count = 0; i < l; i += stride) {
      if (parseInt(pdata[i]) === 0) {
        count++;
      }
    }

    return Math.round((count / total) * 100);
  }

  function getMouse(e, canvas) {
    let offsetX = 0, offsetY = 0, mx, my;

    if (canvas.offsetParent !== undefined) {
      do {
        offsetX += canvas.offsetLeft;
        offsetY += canvas.offsetTop;
      } while ((canvas = canvas.offsetParent));
    }

    mx = (e.pageX || e.touches[0].clientX) - offsetX;
    my = (e.pageY || e.touches[0].clientY) - offsetY;

    return { x: mx, y: my };
  }

  function handlePercentage(filledInPixels) {
    filledInPixels = filledInPixels || 0;
    // console.log(filledInPixels + '%');
    //image reveal %
    if (filledInPixels > 89) {
      cardElement.removeChild(canvas);
      updateScore(cards[index].value);
      checkGameEnd();
    }
  }

  function handleMouseDown(e) {
    isDrawing = true;
    lastPoint = getMouse(e, canvas);
  }

  function handleMouseMove(e) {
    if (!isDrawing) { return; }

    e.preventDefault();

    let currentPoint = getMouse(e, canvas),
      dist = distanceBetween(lastPoint, currentPoint),
      angle = angleBetween(lastPoint, currentPoint),
      x, y;

    for (let i = 0; i < dist; i++) {
      x = lastPoint.x + (Math.sin(angle) * i) - 25;
      y = lastPoint.y + (Math.cos(angle) * i) - 25;
      ctx.globalCompositeOperation = 'destination-out';
      ctx.drawImage(brush, x, y);
    }

    lastPoint = currentPoint;
    handlePercentage(getFilledInPixels(32));
    addSrc();
  }

  function handleMouseUp(e) {
    isDrawing = false;
  }

  if (!selectedCards.includes(index)) {
    selectedCards.push(index);
  }
}

// Update score and progress bar
function updateScore(value) {
  totalScore += value;
  progressFill.style.width = `${(totalScore / 500) * 100}%`;
  progressCount.innerText = `${totalScore}/500$`;
  if (totalScore == 500) {
    getBtn.classList.remove('hidden');
  }
}

// Check if the game is over
function checkGameEnd() {

  if (selectedCards.length >= 7) {

    const emptyBtn = document.querySelector('.popup-empty-btn');
    const partBtn = document.querySelector('.popup-part-btn');
    const allBtn = document.querySelector('.popup-all-btn');

    setTimeout(() => {
      if (totalScore < 500 && totalScore != 0) {
        modalPart.classList.toggle('visibility');
        partBtn.addEventListener('click', initGame);
      }
      if (totalScore == 0) {
        modalEmpty.classList.toggle('visibility');
        emptyBtn.addEventListener('click', initGame);
      }
      if (totalScore == 500) {
        modalAll.classList.toggle('visibility');
        allBtn.addEventListener('click', initGame);
      }
    }, 500);
  }
}

// Event listener for get button (will be redirect?)
// getBtn.addEventListener('click', initGame);

// Initialize the game on page load
initGame();